CREATE FUNCTION get_id_user_post(inTable VARCHAR(45)) RETURNS INT
BEGIN
	DECLARE queryID INT;
    CASE inTable
		WHEN "{DB_PREFIX}users" THEN
			SELECT ID INTO queryID FROM {DB_PREFIX}users ORDER BY RAND() LIMIT 1;
		WHEN "{DB_PREFIX}posts" THEN
			SELECT ID INTO queryID FROM {DB_PREFIX}posts ORDER BY RAND() LIMIT 1;
    END CASE;
    RETURN queryID;
END;



