-- SN_POSTS_CATEGORIES TERMS

CREATE PROCEDURE posts_categories_terms()
BEGIN
    DECLARE post_ID INT;
    DECLARE category_ID INT;
    DECLARE term_ID INT;
    DECLARE count INT DEFAULT 1;
    
    WHILE count <= 50 DO
		SELECT ID INTO post_ID FROM {DB_PREFIX}posts WHERE post_type = 'post' AND ID NOT IN (SELECT relationships_post_ID FROM {DB_PREFIX}posts_categories) AND ID NOT IN (SELECT relationships_post_ID FROM {DB_PREFIX}posts_terms) ORDER BY RAND() LIMIT 1;
		SELECT ID INTO category_ID FROM {DB_PREFIX}categories ORDER BY RAND() LIMIT 1;
		SELECT ID INTO term_ID FROM {DB_PREFIX}terms ORDER BY RAND() LIMIT 1;
		
		SET count = count + 1;
    END WHILE;
END;

CALL posts_categories_terms();
DROP PROCEDURE posts_categories_terms;